#include<stdio.h>
int main()
{
int a,b,temp;
printf("enter the swapping number");
scanf("%d%d",&a,&b);                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        
temp=a;
a=b;
b=temp;
printf("the swapping number"a,b);
}
